	private static final Pattern mailPattern2 = Pattern
			.compile("([\\w\\-]([\\.\\w])+[\\w]+@([\\w\\-]+\\.)+[A-Za-z]{2,4})");

	public static final String convertStringToHTML(String text) {
		String htmlText = StringEscapeUtils.escapeHtml4(text);
		String htmlTextWithLineReturns = htmlText.replace("\n", "<br/>");
		String textWithLinks = applyPattern(htmlTextWithLineReturns,
				urlPattern, 1, "<a href=\"%s\">%s</a>");
		String textWithLinksAndMails = applyPattern(textWithLinks,
				mailPattern2, 0, "<a href=\"mailto:%s\">%s</a>");
		return textWithLinksAndMails;
	}

	private static final String applyPattern(String text, Pattern pattern,
			int delay, String patternResult) {
		Matcher matcher = pattern.matcher(text);
		StringBuilder result = new StringBuilder();
		int lastIndex = 0;
		while (matcher.find()) {
			int matchStart = matcher.start();
			int matchEnd = matcher.end();
			// now you have the offsets of a URL match
			result.append(text.substring(lastIndex, matchStart + delay));
			String link = text.substring(matchStart + delay, matchEnd);
			result.append(String.format(patternResult, link, link));
			lastIndex = matchEnd;
		}
		if (lastIndex <= text.length()) {
			result.append(text.substring(lastIndex));
		}
		return result.toString();
	}

	public static final boolean isUrl(String text) {
		try {
			Matcher matcher = urlPattern.matcher(text);
			matcher.find();
			int matchStart = matcher.start(1);
			int matchEnd = matcher.end();
			return matchStart == 0 && matchEnd > 0 && matchEnd == text.length();
		} catch (IllegalStateException e) {
			return false;
		}
	}
